package net.xiejian.base.example1;

/**
 * 位运算
 * @author jian_xie
 * @date 2015年10月31日
 */
public class BitMain {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int n = 5;
		int result = n >> 3;
		System.out.println(result);
		result = n << 3;
		System.out.println(result);
	}

}
